// Extract the messages array from Graph API response
if ($input.item.json.value && Array.isArray($input.item.json.value)) {
  return $input.item.json.value.map(msg => ({ json: msg }));
}
return [];

// Build OpenAI request payload for email classification (JS-only, paired-item safe)

// Current email from upstream (Graph)
const email = $json || {};
const senderEmail  = email.from?.emailAddress?.address || '';
const senderDomain = (senderEmail.split('@')[1] || '').toLowerCase();
const isInternal   = senderDomain === 'surterreproperties.com';

// Trim to keep tokens under control
const SUBJECT = String(email.subject || '').slice(0, 300);
const BODY    = String(email.bodyPreview || '').slice(0, 4000);

// Lightweight heuristics (we DO NOT decide here; just pass signals to the LLM)
const rx = {
  vendorNoReply: /\b(no-?reply|do-?not-?reply)\b/i,
  otp: /\b(one[-\s]?time|verification|auth(entication)?|code|otp)\b/i,
  unsubscribe: /\bunsubscribe|manage preferences|view in browser\b/i,
  marketing: /\b(newsletter|sale|promotion|webinar|special offer|discount|% off)\b/i,
  finance: /\b(invoice|receipt|order\s?#?\d+|statement|payment)\b/i,
  bounce: /\b(delivery status notification|mail delivery failed|undeliverable)\b/i,
  ooo: /\b(out of office|automatic reply|auto\-reply)\b/i,
  meeting: /\b(invitation|meeting|calendar|event|accepted|declined)\b/i,
  monitoring: /\b(alert|incident|uptime|down|status page|monitoring|alarm)\b/i,
  help: /\b(please help|can you|i need|how do i|i can(?:'|')t|not working|issue|error|stopped working|access|permission)\b/i
};

const hints = {
  isInternalDomain: isInternal,
  signals: {
    vendorNoReply: rx.vendorNoReply.test(senderEmail) || rx.vendorNoReply.test(SUBJECT) || rx.vendorNoReply.test(BODY),
    otp:            rx.otp.test(SUBJECT) || rx.otp.test(BODY),
    unsubscribe:    rx.unsubscribe.test(BODY),
    marketing:      rx.marketing.test(SUBJECT) || rx.marketing.test(BODY),
    finance:        rx.finance.test(SUBJECT) || rx.finance.test(BODY),
    bounce:         rx.bounce.test(SUBJECT) || rx.bounce.test(BODY),
    ooo:            rx.ooo.test(SUBJECT) || rx.ooo.test(BODY),
    meeting:        rx.meeting.test(SUBJECT) || rx.meeting.test(BODY),
    monitoring:     rx.monitoring.test(SUBJECT) || rx.monitoring.test(BODY),
    helpLanguage:   rx.help.test(SUBJECT) || rx.help.test(BODY)
  }
};

// System prompt with richer rules & scenarios (the model still decides)
const systemPrompt = `
Return STRICT JSON only: {"class":"support|vendor|unclear","confidence":0.0-1.0,"reason":"...", "priority":"low|normal|high"}

Definitions
- support: A human is asking IT for help or reporting an IT issue we can act on (login/MFA/lockout, email, Wi-Fi/VPN, printers, PCs/Macs, software install/update, app errors, permissions/shares, device setup, rooms/AV, outages, phishing report, new-user setup, hardware failure).
- vendor: Automated/marketing/transactional/system messages (newsletters, promos, invoices/receipts, OTP codes, do-not-reply notices, shipping, monitoring alerts, CI/CD, ticket status updates, calendar invites, read receipts, delivery failures, OOO auto-replies, social notifications, HR/company-wide announcements with no ask to IT).
- unclear: Only if not enough info and no obvious human help request.

Decision priority (in order)
1) HUMAN INTENT: if a person asks IT for help → support
2) HELP-SEEKING LANGUAGE: "please help", "can you", "i need", "how do i", "i can't", "not working", "issue", "error", "stopped working" → support
3) AUTOMATED PATTERNS: "do not reply", "no-reply", "verify your email", "your code is", "unsubscribe", "receipt/invoice", "delivery failed", "out of office", "meeting invitation", "alert" → vendor
4) DOMAIN: company domain is a strong signal, but content wins; personal email asking for help is still support
5) CONTENT OVER DOMAIN always wins

Priority (only if class = support)
- high: locked out / cannot work, security incident, widespread outage, production/customer impact, "urgent/ASAP", VIP/executive, deadline today
- normal: typical help request without urgency
- low: informational/non-urgent, future-dated, or improvement request

Confidence
- 0.9–1.0 when multiple cues align
- 0.6–0.8 when mixed but leaning one way
- 0.0–0.5 when very little context

Canonical scenarios
- Employee locked out using personal email → support (often high)
- Printer/Teams/Outlook/VPN not working → support
- "Please add me to the X share/Teams/Group" → support
- MFA/verification code email → vendor
- Newsletter / promotion / webinar → vendor
- Out-of-office auto-reply → vendor
- Mail delivery failed / read receipt → vendor
- Calendar invitation / acceptance → vendor
- Monitoring alert from cloud/UptimeRobot/Grafana → vendor (unless a human asks IT for help about it)
- "FYI"/"Thanks" only, no ask → unclear
`;

// User content with context + heuristics (helps the model but doesn't force it)
const userContent = [
  `From: ${senderEmail}${isInternal ? ' (company domain)' : ''}`,
  `Domain: ${senderDomain || 'unknown'}`,
  `Subject: ${SUBJECT}`,
  `Body: ${BODY}`,
  `Heuristics: ${JSON.stringify(hints)}`
].join('\n');

const payload = {
  model: "gpt-3.5-turbo",
  temperature: 0.0,
  response_format: { type: "json_object" },
  messages: [
    { role: "system", content: systemPrompt.trim() },
    { role: "user",   content: userContent }
  ],
  // Keep the original email available to downstream nodes
  _originalEmail: email
};

return { json: payload };

// Parse AI response and merge with original email data
const aiResponse = $input.item.json;
const originalEmail = $('Build AI Request').item.json._originalEmail;
const classification = JSON.parse(aiResponse.choices[0].message.content);

return {
  json: {
    ...classification,
    email: originalEmail
  }
};

// Build ticket creation payload and preserve email ID + access token (paired-item safe)

// Current item's data
const c = $json;                         // same as $input.item.json
const email = c.email || {};

// Read token without relying on paired items
const accessToken = $('Get Access Token').first().json?.access_token;

// Guard rails
if (!email?.id) throw new Error('Build Ticket Payload: missing email.id on current item.');
if (!accessToken) throw new Error('Build Ticket Payload: access token not found from "Get Access Token".');

// Normalize priority
const priorityRaw =
  c.priority ??
  c.classification?.priority ??
  'NORMAL';

const priority = String(priorityRaw).toUpperCase();

// Keep description short/safe (many ticket APIs limit length)
const description = (email.bodyPreview ?? '').toString().slice(0, 5000);

const ticketPayload = {
  title: (email.subject ?? '').toString().slice(0, 255),
  description,
  requesterEmail: email.from?.emailAddress?.address ?? '',
  priority,
  source: 'email',

  // Preserve for next node(s)
  _emailId: email.id,
  _accessToken: accessToken
};

// Keep paired lineage to the current item (optional but harmless)
return {
  json: ticketPayload,
  pairedItem: { item: 0 }
};

// Get email ID and access token from Build Ticket Payload node
const emailId = $('Build Ticket Payload').item.json._emailId;
const accessToken = $('Build Ticket Payload').item.json._accessToken;

return {
  json: {
    emailId: emailId,
    accessToken: accessToken
  }
};

// Prepare email forwarding for non-support emails (paired-item safe)

// Current item's data
const classification = $json;                // same as $input.item.json
const email = classification.email;

// Read token without relying on paired items
const accessToken = $('Get Access Token').first().json?.access_token;

// Guard rails (helpful errors in executions list)
if (!email?.id) throw new Error('Prepare Forward Email: missing email.id on current item.');
if (!accessToken) throw new Error('Prepare Forward Email: access token not found from "Get Access Token".');

return {
  json: {
    emailId: email.id,
    accessToken,
    subject: email.subject ?? '',
    from: email.from?.emailAddress?.address ?? '',
    // use whichever your classifier outputs; fallback to 'unknown'
    classification: classification.class ?? classification.classification ?? 'unknown',
    reason: classification.reason ?? null
  }
};

// Get email ID and access token for marking as read
const emailId = $('Prepare Forward Email').item.json.emailId;
const accessToken = $('Prepare Forward Email').item.json.accessToken;

return {
  json: {
    emailId: emailId,
    accessToken: accessToken
  }
};
