console.log('Title:', ticketData.title);
console.log('Priority:', ticketData.priority);
console.log('Category:', ticketData.category);

return [{
  json: {
    alert: 'HIGH_PRIORITY_DETECTED',
    ticketNumber: ticketData.ticketNumber,
    title: ticketData.title,
    message: `üî• Urgent attention needed for ticket ${ticketData.ticketNumber}`,
    timestamp: new Date().toISOString()
  }
}];

